		<script>
			const form = document.querySelector('.submit-form');
			
			form.addEventListener("submit", (e)=>{
				e.preventDefault();
				return false;
			})
			function f(){
				let headers = {
					'Accept':'application/json',
					'Content-Type':'application/json;charset=utf-8'
				};
				fetch('/lerProduto.php?role=submitForm', 
					  {
					method:'POST',
					headers:headers,
					body:JSON.stringify({
						username: document.querySelector('[name="username"]').value,
						empresa: document.querySelector('[name="empresa"]').value,
						cidade: document.querySelector('[name="cidade"]').value,
						telefone: document.querySelector('[name="telefone"]').value,
						email: document.querySelector('[name="email"]').value,
						message: document.querySelector('[name="msg"]').value
					})
				})
					.then(a=>{return a.json()})
					.then(a=>{ 
						console.log(a);
						document.querySelector('.submit-form').reset();
				}).catch(err=>{
					console.error('Falha no envio da mensagem '+err);
				});
			}
		</script>
